function getCookie(n){const e=n+"=",s=decodeURIComponent(document.cookie),t=s.split(';');for(let s=0;s<t.length;s++){let n=t[s];for(;n.charAt(0)===' ';)n=n.substring(1);if(n.indexOf(e)===0)return n.substring(e.length,n.length)}return""}function setCookie(t,n,s){const e=new Date;e.setTime(e.getTime()+s*24*60*60*1e3);const o="expires="+e.toUTCString();document.cookie=t+"="+n+";"+o+";path=/"}const cookieBanner=document.querySelector('#cookie-banner'),hasCookieConsent=getCookie('cookies-consent');hasCookieConsent||cookieBanner.classList.remove('hidden');const consentCta=cookieBanner.querySelector('#consent-cookies');consentCta.addEventListener('click',()=>{cookieBanner.classList.add('hidden'),setCookie('cookies-consent',1,365)})